mapper.insert:
	INSERT /* acasysStudentRegist.acasysStudentRegistProc */ 
	   INTO TB_STUDENT_INFO 
	   (
	     STUDENT_NO
	   , STUDENT_NAME
	   , STUDENT_AGE
	   , STUDENT_MIDDLE_SCHOOL
	   , STUDENT_HIGH_SCHOOL
	   , STUDENT_SCHOOL_GUBUN_CD
	   , STUDENT_SCHOOL_MAJOR_CD
	   , STUDENT_TIER_STATUS_CD
	   , STUDENT_POST_CD
	   , STUDENT_ADD
	   , STUDENT_ADD_DETAIL
	   , STUDENT_PHONE
	   , STUDENT_NOTE
	   , STUDENT_DEL_YN
	   , REG_USER_ID
	   , REG_DTTM
	   , UPD_USER_ID
	   , UPD_DTTM
	) VALUES (
         (SELECT IFNULL(MAX(CAST(STUDENT_NO AS UNSIGNED)), 0) + 1 AS STUDENT_NO FROM (SELECT STUDENT_NO FROM TB_STUDENT_INFO) AS TEMP)
	   , #{studentName}	  
	   , #{studentAge}	
	   , #{studentMiddleSchool}	
	   , #{studentHighSchool}	  
	   , #{studentSchoolGubunCd}	
	   , #{studentSchoolMajorCd}	
	   , #{studentTierStatusCd}	
	   , #{studentPostCd}	
	   , #{studentAdd}	
	   , #{studentAddDetail}	
	   , #{studentPhone}	
	   , #{studentNote}	
	   , 'N'
	   , #{regUserId}	
	   , NOW()  
	   , #{updUserId}
	   , NOW()
	);

	INSERT /* acasysStudentList.acasysStudentScoreRegistProc */ 
	   INTO TB_STUDENT_SCORE 
	   (
		 STUDENT_NO
	   , STUDENT_NAME
	   , SCORE_NO
	   , START_DATE
	   , END_DATE
	   , TERM_CD
	   , KOREAN
	   , MATH
	   , ENGLISH
	   , SOCIETY
	   , SCIENCE
	   , HISTORY
	   , REG_USER_ID
	   , REG_DTTM
	   , UPD_USER_ID
	   , UPD_DTTM
	) VALUES (
         #{studentNo}
	   , #{studentName}	  
	   , (SELECT IFNULL(MAX(CAST(SCORE_NO AS UNSIGNED)), 0) + 1 AS SCORE_NO FROM (SELECT SCORE_NO FROM TB_STUDENT_SCORE) AS TEMP)
	   , #{startDate}	
	   , #{endDate}	
	   , #{termCd}	  
	   , #{korean}	
	   , #{math}	
	   , #{english}	
	   , #{society}	
	   , #{science}	
	   , #{history}	
	   , #{regUserId}	
	   , NOW()  
	   , #{updUserId}
	   , NOW()
	);

mapper.update:
UPDATE /* acasysStudentDetail.acasysStudentDetailUpdateProc */
     	   TB_STUDENT_SCORE
     SET START_DATE = #{startDate}
	     , END_DATE = #{endDate}
	     , TERM_CD = #{termCd}
	     , KOREAN = #{korean}
	     , MATH = #{math}
	     , ENGLISH = #{english}
	     , SOCIETY = #{society}
	     , SCIENCE = #{science}
	     , HISTORY = #{history}
	     , UPD_USER_ID = #{updUserId}
	     , UPD_DTTM = NOW()
 WHERE STUDENT_NO = #{studentNo}
 	 AND STUDENT_NAME = #{studentName} 
 	 AND SCORE_NO = #{scoreNo}

UPDATE /* acasysStudentDetail.acasysStudentDetailUpdateProc */
     	   TB_STUDENT_INFO
     SET STUDENT_NAME = #{studentName}
	     , STUDENT_AGE = #{studentAge}
	     , STUDENT_MIDDLE_SCHOOL = #{studentMiddleSchool}
	     , STUDENT_HIGH_SCHOOL = #{studentHighSchool}
	     , STUDENT_SCHOOL_GUBUN_CD = #{studentSchoolGubunCd}
	     , STUDENT_SCHOOL_MAJOR_CD = #{studentSchoolMajorCd}
	     , STUDENT_TIER_STATUS_CD = #{studentTierStatusCd}
	     , STUDENT_POST_CD = #{studentPostCd}
	     , STUDENT_ADD = #{studentAdd}
	     , STUDENT_ADD_DETAIL = #{studentAddDetail}
	     , STUDENT_PHONE = #{studentPhone}
	     , STUDENT_NOTE = #{studentNote}
	     , STUDENT_DEL_YN = 'N'
	     , UPD_USER_ID = #{updUserId}
	     , UPD_DTTM = NOW()
 WHERE STUDENT_NO = #{studentNo}
